@section('script')
<script>
    var product_id = null;

    $(document).on('click', '.order', function(){
        $('#formModal').modal('show');
        $('#myForm')[0].reset();
        $('.form-control').removeClass('is-invalid');
        product_id = $(this).attr('product_id');

        $.ajax({
            url :"/customer/product/"+product_id,
            dataType:"json",
            beforeSend:function(){
                $("#action_button").attr("disabled", true);
                $("#action_button").attr("value", "LOADING..");  
            },
            success:function(data){
                $("#action_button").attr("disabled", false);
                $("#action_button").attr("value", "ORDER");
                $.each(data.product, function(key,value){
                    if(key == 'name'){
                        $('#product_name').text(value)
                    }
                    if(key == 'category'){
                        $('#category').text(value)
                    }
                    if(key == 'image'){
                        $('#image').attr("src", '/assets/img/products/' + value);
                    }
                    var sps = "";
                    $.each(data.sps, function(key,value){
                        if(key == 0){
                            sps += '<label class="btn btn-secondary active">';
                                sps += '<input type="radio" name="size" autocomplete="off" checked value="'+value.size_id+'"> ' + value.size + ' â‚± ' + value.price + ' STOCK: ('+value.stock+')';
                            sps += '</label>'; 
                        }else{
                            sps += '<label class="btn btn-secondary">';
                                sps += '<input type="radio" name="size" autocomplete="off" value="'+value.size_id+'"> ' + value.size + ' â‚± ' + value.price + ' STOCK: ('+value.stock+')';
                            sps += '</label>'; 
                        }
                                   
                    })
                    $('#sps').empty().append(sps);
                })
            },
            error:function(){
                window.location.href = "/login";
            } 
        })

    });

    $('#formModal').on('shown.bs.modal', function () {
        $('#qty').focus();
    }) 

    $('#myForm').on('submit', function(event){
        event.preventDefault();
        $('.form-control').removeClass('is-invalid')

        $.ajax({
            url :"/customer/product/"+product_id,
            method:"POST",
            data:$(this).serialize(),
            dataType:"json",
            beforeSend:function(){
                $("#action_button").attr("disabled", true);
                $("#action_button").attr("value", "LOADING..");  
            },
            success:function(data){
                $("#action_button").attr("disabled", false);
                $("#action_button").attr("value", "ORDER");

                if(data.errors){
                    $.each(data.errors, function(key,value){
                        if(key == $('#'+key).attr('id')){
                            $('#'+key).addClass('is-invalid')
                            $('#error-'+key).text(value)
                        }
                    })
                }
                if(data.errorstock){
                    $('#qty').addClass('is-invalid');
                    $('#error-qty').text(data.errorstock);
                }
                if(data.success){
                    $.confirm({
                    title: 'Confirmation',
                    content: data.success,
                    type: 'green',
                    buttons: {
                            
                            order: {
                                text: 'Order Again',
                                btnClass: 'btn-blue',
                                keys: ['enter', 'shift'],
                                action: function(){
                                    location.reload();
                                }
                            },

                            checkout: {
                                text: 'Check Out',
                                btnClass: 'btn-primary',
                                action: function(){
                                    window.location.href = "/customer/orders";
                                }
                            },
                        }
                    });
                }
               
            },
        });
    });


    $('#search-bar').on("input", function() {
        var filter = 'search';
        var value = this.value;

        $.ajax({
        url: "/customer/products/filter", 
        type: "get",
        dataType:"json",
        data: {
            filter:filter, value:value ,_token: '{!! csrf_token() !!}',
        },
        beforeSend: function() {
            
        },
        success: function(data){
                if(data.products){
                        var products = '';
                        $.each(data.products, function(key,value){
                            products += '<div class="col mb-5">'
                                products += '<div class="card h-100">'
                                    products += '<div class="badge bg-dark text-white position-absolute text-uppercase" style="top: 0.5rem; right: 0.5rem">'+value.category+'</div>'
                                    products += '<img class="card-img-top" width="200" height="190" src="/assets/img/products/'+value.image+'" alt="'+value.image+'" />'
                                        products += '<div class="card-body p-4">'
                                            products += '<div class="text-center">'
                                                products += '<h5 class="fw-bolder">'+value.name+'</h5>'
                                                
                                            products += '</div>'
                                        products += '</div>'
                                    products += '<div class="card-footer p-4 pt-0 border-top-0 bg-transparent">'
                                    products += '<div class="text-center"><button class="btn btn-outline-primary mt-auto order" product_id="'+value.id+'">ORDER</button></div>'
                                    products += '</div>'
                                products += '</div>'
                            products += '</div>'          
                        });
                        $('#product_list').empty().append(products);
                    }
                },
            });
    });


    $('#filter_category').on("change", function(event){
        var filter = 'category';
        var value = this.value;

        $.ajax({
        url: "/customer/products/filter", 
        type: "get",
        dataType:"json",
        data: {
            filter:filter, value:value ,_token: '{!! csrf_token() !!}',
        },
        beforeSend: function() {
            
        },
        success: function(data){
                    if(data.products){
                        var products = '';
                        $.each(data.products, function(key,value){
                            products += '<div class="col mb-5">'
                                products += '<div class="card h-100">'
                                    products += '<div class="badge bg-dark text-white position-absolute text-uppercase" style="top: 0.5rem; right: 0.5rem">'+value.category+'</div>'
                                    products += '<img class="card-img-top" width="200" height="190" src="/assets/img/products/'+value.image+'" alt="'+value.image+'" />'
                                        products += '<div class="card-body p-4">'
                                            products += '<div class="text-center">'
                                                products += '<h5 class="fw-bolder">'+value.name+'</h5>'
                                                
                                            products += '</div>'
                                        products += '</div>'
                                    products += '<div class="card-footer p-4 pt-0 border-top-0 bg-transparent">'
                                    products += '<div class="text-center"><button class="btn btn-outline-primary mt-auto order" product_id="'+value.id+'">ORDER</button></div>'
                                    products += '</div>'
                                products += '</div>'
                            products += '</div>'          
                        });
                        $('#product_list').empty().append(products);
                    }
                  
                },
            });
    });
    
</script>
